<?phpuse yii\helpers\Html;use yii\bootstrap\ActiveForm;use yii\captcha\Captcha;$this->title = Yii::t('app','Árajánlat kérése');?><div class="productsopinion-form">    <button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>    <h1><?= Html::encode($this->title) ?></h1>    <div id="enquire-form-container">        <?php $form = ActiveForm::begin(['id' => 'enquire-form']); ?>            <?= $form->field($model, 'name')->textInput() ?>            <?= $form->field($model, 'email') ?>            <?= $form->field($model, 'body')->textArea(['rows' => 6]) ?>            <?= $form->field($model, 'product_id')->hiddenInput()->label(false) ?>            <?php /*$form->field($model, 'verifyCode')->widget(Captcha::className(), [                'template' => '<div class="row"><div class="col-lg-3">{image}</div><div class="col-lg-6">{input}</div></div>',            ])*/ ?>            <div class="form-group">                <?= Html::submitButton(Yii::t('app','Küldés'), ['class' => 'btn btn-primary', 'name' => 'contact-button']) ?>            </div>        <?php ActiveForm::end(); ?>    </div></div><?php$srcipt = <<< JS$('form#enquire-form').on('beforeSubmit', function(e){    var \$form = $(this);    $.post(        \$form.attr("action"), //serialize Yii2 form        \$form.serialize()    )        .done(function(result) {        if (typeof(result.succes) != "undefined" || result.succes != null) {            if(result.succes == 1)            {                $("#enquire-form-container").html(result.content);            } else {                $("#enquire-form-container").html(result.content);            }        }        }).fail(function()        {            console.log("server error");        });    return false;});JS;$this->registerJs($srcipt);?>