<?phpuse yii\helpers\Html;use yii\widgets\ActiveForm;use lajax\translatemanager\models\Language;$this->title = Yii::t('app','Felhasználási feltételek');?><div class="terms-form">    <h1><?= Yii::t('app','Felhasználási feltételek') ?></h1>    <?php $form = ActiveForm::begin([        'id' => 'profile-form',    ]); ?>        <ul class="nav nav-tabs">        <li class="active"><a href="#content" data-toggle="tab"><?= Yii::t('app','Tartalom') ?></a></li>        <li><a href="#translate" data-toggle="tab"><?= Yii::t('app','Fordítás') ?></a></li>    </ul>    <div class="tab-content">        <div class="tab-pane active" id="content">            <?= $form->field($terms, 'content')->widget(\yii\redactor\widgets\Redactor::className(),[                'clientOptions' => [                    'lang' => Yii::$app->language,                    'buttons' => [/*'html',*/ 'formatting', 'bold', 'italic', 'deleted', 'unorderedlist', 'orderedlist', 'outdent', 'indent', /*'image', 'file',*/ 'link', 'alignment', 'horizontalrule'],                ]            ]) ?>                    </div>                <div class="tab-pane" id="translate">                        <ul class="nav nav-tabs">                <?php                 $first=true;                foreach (Language::getLanguages() as $language) {                    if($language->language_id!=$terms->lang_code) {                        $langId=$language->language_id;                        $liclass=($first)?' class="active"':'';                        if($first) $firstlang=$language->language_id;                        $first=false;                        echo '<li'.$liclass.'><a href="#'.$langId.'" data-toggle="tab">'.$language->name.'</a></li>';                    }                }                ?>            </ul>                        <div class="tab-content">                 <?php                                 foreach ($termsTranslations as $langId=>$translation) {                $liclass=($firstlang==$translation->lang_code)?' active':'';                ?>                <div class="tab-pane<?= $liclass ?>" id="<?= $translation->lang_code ?>">                                    <?php //$form->field($translation, "[{$langId}]lang_code")->hiddenInput()->label(false); ?>                                    <?= $form->field($translation, "[{$langId}]content")->widget(\yii\redactor\widgets\Redactor::className(),[                        'clientOptions' => [                        'lang' => Yii::$app->language,                        'buttons' => [/*'html',*/ 'formatting', 'bold', 'italic', 'deleted', 'unorderedlist', 'orderedlist', 'outdent', 'indent', /*'image', 'file',*/ 'link', 'alignment', 'horizontalrule'],                   ]                ]) ?>                                </div>                                <?php } ?>                            </div>        </div>            </div>    <div class="form-group">        <?= Html::submitButton(Yii::t('app', 'Módosítás'), ['class' => 'btn btn-primary']) ?>    </div>    <?php ActiveForm::end(); ?></div>